import React, { useState } from "react";
import TweetButton from "../../UI/button";

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const { email, password } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="text-white mx-auto placeholder-opacity-100 w-1/3">
      <h1>Sign In</h1>
      <p>Sign into your Account</p>
      <form onSubmit={(e) => onSubmit(e)}>
        <div className="grid grid-cols-1 gap-2">
          <label className="block">
            <span className="text-white">Email</span>
            <input
              type="email"
              className="mt-1 block w-full text-black rounded-md"
              placeholder=""
              name="email"
              onChange={(e) => onChange(e)}
              required
            />
          </label>
          <label className="block">
            <span className="text-white">Password</span>
            <input
              type="password"
              className="mt-1 block w-full text-black rounded-md placeholder-gray-500"
              placeholder=""
              name="password"
              onChange={(e) => onChange(e)}
              minLength="6"
              required
            />
          </label>
          <div className="pt-4 flex justify-center">
            <div className="flex">
              <TweetButton>Login</TweetButton>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
