import React from "react";
import img from "../../static/twitter-icon-svg.jpg";
import { HiEllipsisHorizontal } from "react-icons/hi2";

import Avatar from "../Avatar";

const ViewPage = ({ children, visible, setVisible }) => {
  var display = "hidden";
  if (visible) {
    display = "";
  } else {
    display = "hidden";
  }
  return (
    <div className={`flex justify-end ${display}`}>
    {/* <div className="flex justify-end display"> */}
      <div className="w-full bg-red-700 bg-opacity-50 text-white">exit</div>
      <div className="w-[350px] bg-red-200 px-4 pt-3 mb-1">
        <div className="flex w-full ">
          <Avatar />
          <div className="flex justify-between w-full">
            <div className="block items-center ">
              <div>name</div>
              <div>@username</div>
            </div>
            <HiEllipsisHorizontal className="w-7 h-7 on-hover-sm" />
          </div>
        </div>
        <div>text</div>
      </div>
    </div>
  );
};

export default ViewPage;
